import React, { Component } from 'react';
import axios from 'axios'

class SubCategories extends Component {
  handleClick(e){
    console.log(e.target)
    this.findProducts(e.target.id);
  }

  findProducts(id){
    axios.get(`http://localhost:8080/category/find/${id}`)
      .then(response => {
          if (!response.data.errmsg) {
            console.log('find products successful')
            this.props.updateProducts(response.data);
          } else {
            console.log('find products failed')
          }
      }).catch(error => {
              console.log('find error: ')
              console.log(error)
      });
  }
  componentDidMount(){
    this.props.getSubCategories(this.props.parent_category);
  }
  render() {
    const mapped_categories = this.props.sub_categories.map((a) =>{
      if(a){
        return (
          <div key={a.category_id} className={"ui_category ui item"+a.category_id}>
            <div id={a.category_id} className="" onClick={(e)=>{this.handleClick(e)}}>{a.category_name}</div>
          </div>
        )
      } else{
        return null
      }
    });
    return (
      <main>
        <div className="categories_div">
                {mapped_categories}
        </div>
      </main>
    );
  }
}

export default SubCategories;
