import React, { Component } from 'react';
import axios from 'axios'

import Header from './components/Header'
import Product from './components/Product'
class App extends Component {
  state={
    categories: [],
    products: [],
  }
  componentDidMount(){
    this.getProducts();
  }
  deleteProduct(id){
    const ref = this;
    axios.delete(`http://localhost:8080/product/delete/${id}`)
      .then(response => {
          if (!response.data.errmsg) {
            console.log('delete product successful')
            ref.getProducts();
          } else {
            console.log('delete product failed')
          }
      }).catch(error => {
              console.log('delete error: ')
              console.log(error)
      });
  }
  getProducts(){
    axios.get(`http://localhost:8080/product/all`)
    .then(response => {
        if (!response.data.errmsg) {
          this.setState({products : response.data})
          console.log(response.data)
          console.log('get products successful')
        } else {
          console.log('get products failed')
        }
    }).catch(error => {
            console.log('error: ')
            console.log(error)
    })
}
  render() {
    return (
      <div className="App">
        <Header />
        <Product products={this.state.products} getProducts={this.getProducts} deleteProduct={this.deleteProduct} />
      </div>
    );
  }
}

export default App;
