import React, { Component } from 'react';
import axios from 'axios'
import backend_path from './backend'
class SubCategories extends Component {
  state={
    sub_categories:[],
  }
  handleClick(e){
    console.log(e.target)
    console.log(e.target)
    var els = document.querySelectorAll('.active')
    for (var i = 0; i < els.length; i++) {
      els[i].classList.remove('active')
    }
    e.target.className = "active";
    this.findProducts(e.target.id);
  }

  findProducts(id){
    axios.get(`${backend_path}/category/find/${id}`)
      .then(response => {
          if (!response.data.errmsg) {
            console.log('find products successful')
            this.props.updateProducts(response.data);
          } else {
            console.log('find products failed')
          }
      }).catch(error => {
              console.log('find error: ')
              console.log(error)
      });
  }

  render() {
    const mapped_categories = this.state.sub_categories.map((a) =>{
        return (
          <li key={a.id} className={"sub_category "+a.category_id}>
            <div id={a.id} className="" onClick={(e)=>{this.handleClick(e)}}>{a.name}</div>
          </li>
        )
    });
    return (
      <main>
        <ul className="subcategories_div">
                {mapped_categories}
        </ul>
      </main>
    );
  }
}

export default SubCategories;
