import React, { Component } from 'react';
import axios from 'axios'

class Categories extends Component {
  handleClick(e){
    console.log(e.target)
    this.findProducts(e.target.id);
  }

  findProducts(id){
    axios.get(`http://localhost:8080/category/find/${id}`)
      .then(response => {
          if (!response.data.errmsg) {
            console.log('find products successful')
            this.props.updateProducts(response.data);
          } else {
            console.log('find products failed')
          }
      }).catch(error => {
              console.log('find error: ')
              console.log(error)
      });

  }
  mapped_sub_categories = ()=> this.props.sub_categories.map((a) =>{
    if(a){
      return (
        <div key={a.category_id} className={"ui_category ui item"+a.category_id}>
          <div id={a.category_id} className="" onClick={(e)=>{this.handleClick(e)}}>{a.category_name}</div>
        </div>
      )
    }else{
      return null;
    }
  });
  mapped_categories = () => this.props.categories.map((a) =>{
    if(!a.parent_category){
      return (
        <div key={a.category_id} className={"ui_category ui item"+a.category_id}>
          <div id={a.category_id} className="" onClick={(e)=>{this.handleClick(e)}}>{a.category_name}</div>
          {this.props.getSubCategories(a.category_id)}
          {this.mapped_sub_categories}
        </div>
      )
    } else{
      return null;
    }
  });
  render() {
    return (
      <main>
        <div className="categories_div">
                <div  className={"ui_category"}>
                <div className="" onClick={this.props.getProducts}>All</div>
                </div>
                {this.mapped_categories}
        </div>
      </main>
    );
  }
}

export default Categories;
