import React, { Component } from 'react';


class Product extends Component {
  state={
    categories: [],
  }
  render() {
    const mapped_categories = this.state.categories.map((a)=>{
      return (
        <a>{a.category_name}</a>
      )
    })
    const mapped_products = this.props.products.map((a) =>{
      return (
        <div className="ui card">
          <div className="image dimmable">
            <img src="images/product_icon.png"/>
          </div>
          <div className="content">
            <div className="header">{a.product_name}</div>
            <div className="meta">
              <a className="group">{a.product_description}</a>
            </div>
            {this.setState({categories: a.categories})}
            <div className="description">{mapped_categories}</div>
          </div>
          <div className="ui one bottom attached buttons">
            <div className="ui button"> DELETE </div>
          </div>
        </div>
      )
    });
    return (
      <main>
        <div className="ui four cards">
                {mapped_products}
        </div>
      </main>
    );
  }
}

export default Product;
