import React, { Component } from 'react';
import axios from 'axios'

class Categories extends Component {
  state={
    sub_categories:[],
  }
  handleClick(e){
    console.log(e.target)
    this.findProducts(e.target.id);
  }
  getSubCategories(id){
    axios.get(`http://localhost:8080/category/sub/${id}`)
      .then(response => {
          if (!response.data.errmsg) {
            console.log('get sub categories successful')
            this.setState({sub_category: response.data});
          } else {
            console.log('get sub categories failed')
          }
      }).catch(error => {
              console.log('get sub categories error: ')
              console.log(error)
      });
  }
  findProducts(id){
    axios.get(`http://localhost:8080/category/find/${id}`)
      .then(response => {
          if (!response.data.errmsg) {
            console.log('find products successful')
            this.props.updateProducts(response.data);
          } else {
            console.log('find products failed')
          }
      }).catch(error => {
              console.log('find error: ')
              console.log(error)
      });

  }
  render() {
    const mapped_sub_categories = this.state.sub_categories.map((a) =>{
        return (
          <div key={a.category_id} className={"ui_category ui item"+a.category_id}>
            <div id={a.category_id} className="" onClick={(e)=>{this.handleClick(e)}}>{a.category_name}</div>
          </div>
        )
    });
    const mapped_categories = this.props.categories.map((a) =>{
      if(!a.parent_category){
        this.getSubCategories(a.category_id).bind(this)
        return (
          <div key={a.category_id} className={"ui_category ui item"+a.category_id}>
            <div id={a.category_id} className="" onClick={(e)=>{this.handleClick(e)}}>{a.category_name}</div>
            {/* {} */}
            {mapped_sub_categories}
          </div>
        )
      } else{
        return null;
      }
    });
    return (
      <main>
        <div className="categories_div">
                <div  className={"ui_category"}>
                <div className="" onClick={this.props.getProducts}>All</div>
                </div>
                {mapped_categories}
        </div>
      </main>
    );
  }
}

export default Categories;
