import React, { Component } from 'react';
import axios from 'axios'
import backend_path from './backend'
class SubCategories extends Component {
  state={
    sub_categories:[],
  }
  handleClick(e){
    console.log(e.target)
    this.findProducts(e.target.id);
  }

  findProducts(id){
    axios.get(`${backend_path}/category/find/${id}`)
      .then(response => {
          if (!response.data.errmsg) {
            console.log('find products successful')
            this.props.updateProducts(response.data);
          } else {
            console.log('find products failed')
          }
      }).catch(error => {
              console.log('find error: ')
              console.log(error)
      });
  }
  getSubCategories(id){
    axios.get(`${backend_path}/category/sub/${id}`)
      .then(response => {
          if (!response.data.errmsg) {
            console.log('get sub categories successful')
            console.log(response.data)
            this.setState({sub_categories: response.data});
          } else {
            console.log('get sub categories failed')
          }
      }).catch(error => {
              console.log('get sub categories error: ')
              console.log(error)
      });
  }
  componentDidMount(){
    this.getSubCategories(this.props.parent_category);
  }
  render() {
    const mapped_categories = this.state.sub_categories.map((a) =>{
        if(!this.getSubCategories(a.category_id))
        if(this.getSubCategories(a.category_id)[0])
        return (
          <li key={a.category_id} className={"sub_category "+a.category_id}>
            <div id={a.category_id} className="" onClick={(e)=>{this.handleClick(e)}}>{a.category_name}</div>
          </li>
        );
        else{
          return(
          <li key={a.category_id} className={"sub_category "+a.category_id}>
            <div id={a.category_id} className="" onClick={(e)=>{this.handleClick(e)}}>{a.category_name}</div>
            <SubCategories updateProducts={this.props.updateProducts} parent_category={a.category_id}/>
          </li>
          );
        }
    });
    return (
      <main>
        <ul className="subcategories_div">
                {mapped_categories}
        </ul>
      </main>
    );
  }
}

export default SubCategories;
